// Get all unique artifact IDs for API fetching
export function getAllArtifactIds(): string[] {
  const ids = new Set<string>()
  for (const recipe of GEAR_RECIPES) {
    if (recipe.artifactId) {
      ids.add(recipe.artifactId)
    }
  }
  return Array.from(ids)
}

// Get item ID for a specific tier/enchant (format: T4_MAIN_SWORD, T5_MAIN_SWORD@1, etc.)
export function getGearItemId(baseId: string, tier: number, enchant: number = 0): string {
  const itemId = `T${tier}_${baseId}`
  return enchant > 0 ? `${itemId}@${enchant}` : itemId
}

// Get material item ID for a specific tier/enchant
export function getMaterialItemId(matType: MaterialType, tier: number, enchant: number = 0): string {
  const baseId = MATERIAL_ITEM_IDS[matType][tier]
  return enchant > 0 ? `${baseId}@${enchant}` : baseId
}

// Group recipes by category
export function getRecipesByCategory(): Record<GearCategory, GearRecipe[]> {
  const grouped: Record<GearCategory, GearRecipe[]> = { 1: [], 2: [], 3: [], 4: [] }
  for (const recipe of GEAR_RECIPES) {
    grouped[recipe.category].push(recipe)
  }
  return grouped
}

// Get recipes by item type
export function getRecipesByItemType(itemType: GearItemType): GearRecipe[] {
  return GEAR_RECIPES.filter(r => r.itemType === itemType)
}
